import React from 'react'
import SingleNote from './SingleNote';

class NewNoteButton extends React.Component{

    constructor(props){
        super(props);
        this.state = { isShown: false };
        this.displayNewNote = this.displayNewNote.bind(this);
    }

    displayNewNote(e) {
        e.preventDefault();

        this.setState({isShown: true});
    }
    saveNote(e){
        //Save note to database
        e.preventDefault();
        this.setState({isShown: false});
    }

    render(){
        return (
            <div name="searchbutton">
                <button id="newnote" onClick={this.displayNewNote}>New Note</button>
                {this.state.isShown && <SingleNote /> && <button id="save" onClick={this.saveNote}>Submit</button>}
            </div>
        )
    }
}

export default NewNoteButton
